mapper.insert:
INSERT INTO general_board(

) VALUES (
      #{boardSeq}
    , #{boardTitle}
    , #{boardText}
    , #{boardWriter}
    , NOW()
    , NULL
    , NULL
    , 0
)

mapper.update:
UPDATE general_board
SET
      board_mod_date = now()
    , board_title = #{boardTitle}
    , board_text = #{boardText}
    , board_writer = #{boardWriter}
WHERE
    board_seq = #{boardSeq}
